sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.statusCode == "200") {

    var payload = JSON.parse(inputs.responseBody);
    outputs.project_count = payload.length;

  } else {
    outputs.project_count = 0;
  }
  gs.info('Project count = ' + outputs.project_count);

})(inputs, outputs);

